        public async Task AddArtist(Artist a)
        {
            await _context.AddAsync<Artist>(a);
            _context.SaveChanges();
        }

        public async Task AddUser(UserArt u)
        {
            await _context.AddAsync<UserArt>(u);
            _context.SaveChanges();
        }

        public async Task<bool> CheckIfUserExist(string id)
        {
