            UserArt user= await _context.UserArts.FirstOrDefaultAsync(u => u.UserId == id);
            if (user != null)
            {
                b= true;
            }
            Artist artist = await _context.Artists.FirstOrDefaultAsync(u => u.ArtistId == id);
            if (artist != null)
            {
                b = true;
            }
            return b;
        }
