        public async Task<Artist> GetArtist(string id)
        {
            return await _context.Artists.FirstOrDefaultAsync(a => a.ArtistId.Equals(id));
        }

        public async Task<UserArt> GetUserById(string id)
        {
            return await _context.UserArts.FirstOrDefaultAsync(a => a.UserId.Equals(id));
        }
        public async Task<Artist> GetArtistByArtistId(string id)
        {
            return await _context.Artists.FirstOrDefaultAsync(a => a.ArtistId.Equals(id));
        }

        public async Task<Artist> GetArtistById(int id)
        {
            return await _context.Artists.FirstOrDefaultAsync(a => a.Id ==id);
        }

        public async Task<Uri> GetUri(int id)
        {
            var imageFilename = Guid.NewGuid().ToString();
            var imagePath = $"{storageService.StorageBaseUri}{imageFilename}";
            Artist a = await _context.Artists.FirstOrDefaultAsync(a => a.Id == id);
            a.ImgPath = imagePath;
            _context.SaveChanges();
            var uploadUri = storageService.CreateUploadUri(imageFilename);
            return uploadUri;
        }
        public async Task UpdateDesc(Artist artist)
        {
            Artist a = await _context.Artists.FirstOrDefaultAsync(a => a.Id == artist.Id);
            a.Description = artist.Description;
            _context.SaveChanges();

        }
        public async Task UpdateBank(Artist artist)
        {
            Artist a = await _context.Artists.FirstOrDefaultAsync(a => a.Id == artist.Id);
            a.Iban = artist.Iban;
            a.Bic = artist.Bic;
            _context.SaveChanges();

        }
